mapper.select:

SELECT h.*,v.goods_name FROM uview h
  join member u on h.user_id=u.user_id
  join goods v on h.goods_id=v.goods_id




	uview_id = #{uview_id}




	and h.user_id = #{user_id}




	and h.goods_id = #{goods_id}


